import numpy as np
rainbow_colors = [(255, 0, 0), (255, 165, 0), (255, 255, 0), (0, 255, 0), (0, 0, 255), (75, 0, 130), (148, 0, 211)]
def interpolate_color(start_color, end_color, steps):
    r_diff = (end_color[0] - start_color[0]) / steps
    g_diff = (end_color[1] - start_color[1]) / steps
    b_diff = (end_color[2] - start_color[2]) / steps

    interpolated_colors = []
    for step in range(1, steps):
        r = int(start_color[0] + r_diff * step)
        g = int(start_color[1] + g_diff * step)
        b = int(start_color[2] + b_diff * step)
        interpolated_colors.append((r, g, b))

    return interpolated_colors

def expand_color_array(colors, length):
    expanded_colors = []
    steps = length // (len(colors) - 1)

    for i in range(len(colors) - 1):
        start_color = colors[i]
        end_color = colors[i + 1]

        expanded_colors.append(start_color)
        interpolated_colors = interpolate_color(start_color, end_color, steps)
        expanded_colors.extend(interpolated_colors)

    expanded_colors.append(colors[-1])

    return expanded_colors[:length]

# 將 rainbow_colors 擴增為長度 250
expanded_rainbow = expand_color_array(rainbow_colors, 1000)

